					if(check(k|i|(j<<1)|j|(j>>1))) continue;//要满足上一行铺满。
					dp[h][i][j]=min(dp[h][i][j],dp[h-1][j][k]+tot);
				}
			}
		}
	}
	int ans=0x3f3f3f3f;
	for(int i=0;i<(1<<n);i++){
		for(int j=0;j<(1<<n);j++){
			if(check(i|(i<<1)|(i>>1)|j)) continue;//统计答案时最后一行要铺满。
			ans=min(ans,dp[m][i][j]);
		}
	}
	cout<<n*m-ans;//输出的是 n * m 减去这个最小值。
	return 0;
}
