	for(int i=1; i<n; i++) {
		int u,v;
		scanf("%lld%lld",&u,&v);
		insert(u,v);
		insert(v,u);
	}
	dfs(1);
	for(int i=2; i<=n; i++) makeroot(i,i-1);
	int tot=n;
	for(int i=1; i<=m; i++) {
		scanf("%s",opt);
		if(opt[0]=='c') {
			int x,y;
			scanf("%lld%lld",&x,&y);
		    splay(x);
			int sx=siz[c[x][1]]+1+tr.query(val[x]-1);
			splay(y);
			int sy=siz[c[y][1]]+1+tr.query(val[y]-1);
			if(sx<sy) printf("%lld\n",x);
			else printf("%lld\n",y);
		} 
		else if(opt[0]=='u'){
			int x;
			scanf("%lld",&x);
			makeroot(x,tot++);
		} else {
			int x;
			scanf("%lld",&x);
			splay(x);
			printf("%lld\n",siz[c[x][1]]+1+tr.query(val[x]-1));
		}
	}
	return 0;
}
