//字符串模拟
#include<iostream>
#include<cstdio>
#include<map>//可以使用map来判重
#include<cstring>
using namespace std;
int n;
string a;
map<char,int>p;
int main(){
	int t,f,s;
	cin>>n;
	for(int i=1;i<=n;i++){
		cin>>a;//读入字符串
		f=t=s=0;
		for(int i=0;i<a.size();i++)
			p[a[i]]++;
		for(char i='a';i<='z';i++){
			if(p[i]>1){
				f=1;
				cout<<"No"<<endl;
				break;
			}//如果一个字母出现次数大于一次，那么直接输出No
			else if(p[i]!=0)
				t++;//否则记录连续出现的字符长度
			else
				t=0;
			s=max(s,t);//s记录最长长度，记住初始化
		}
		if(s==a.size())
			cout<<"Yes"<<endl;//如果连续字符长度与字符串相等，那么输出Yes
		if(s<a.size()&&f==0)
			cout<<"No"<<endl;//否则输出No
		for(char i='a';i<='z';i++)
			p[i]=0;//初始化
	}
	return 0;
}
```

