const int MAXN = 2e5 + 6;
typedef long long ll;
const int P = 998244353;
int n , k;

int pri[10000006] , mnp[10000006] , mu[10000006] , cn = 0;
void sieve( ) {
	mu[1] = 1;
	rep( i , 2 , 1e7 ) {
		if( !pri[i] ) pri[++cn] = i , mu[i] = -1 , mnp[i] = i;
		for( int j = 1 ; j <= cn && pri[j] * i < 10000000 ; ++j ) {
			mnp[i * pri[j]] = pri[j] , pri[i * pri[j]] = 1;
			if( i % pri[j] == 0 ) { mu[i * pri[j]] = 0; break; }
			mu[i * pri[j]] = -mu[i];
		}
	}
}

int buc[10000006];
vi lsj[MAXN];
int deg[MAXN];
int A[MAXN];
set<int> zzh;
int era[MAXN];
void caldeg( int r ) {
	rep( i , 1 , r ) if( !era[i] ) for( int x : lsj[i] ) ++ buc[x];
	rep( i , 1 , n ) deg[i] = 0;
	rep( i , 1 , r ) if( !era[i] ) {
		for( int x : lsj[i] ) deg[i] += mu[x] * buc[x];
	}
	rep( i , 1 , r ) if( !era[i] ) for( int x : lsj[i] ) buc[x] = 0;
}
int cal( int r ) {
	caldeg( r );
	int as = 0;
	rep( i , 1 , r ) if( deg[i] ) {
		++ as;
	}
	return as;
}

int W[300];
int gcd( int a , int b ) {
	return !b ? a : gcd( b , a % b );
}

void solve() {
	cin >> n >> k;
	rep( i , 1 , n ) scanf("%d",A + i);
	rep( i , 1 , n ) {
		vi f;
		int c = A[i];
		while( c != 1 ) {
			int p = mnp[c];
			f.pb( p );
			while( c % p == 0 ) c /= p;
		}
		W[0] = 1;
		lsj[i].pb( 1 );
		for( int t = 1 ; t < ( 1 << f.size() ) ; ++ t )
			W[t] = W[t ^ ( t & -t )] * f[__builtin_ctz( t )] , lsj[i].pb( W[t] );
	}
	int ff = 0;
	caldeg( n );
	int ng = 0 , fk = 0;
	rep( i , 1 , n ) {
		if( deg[i] == 0 ) ++ ng , era[i] = 1;
		if( deg[i] > 2 ) fk = i;
	}
	if( ng >= k ) {
		vi as;
		rep( i , 1 , n ) if( deg[i] == 0 ) as.pb( i );
		as.resize( k );
		for( int x : as ) printf("%d ",x); puts("");
		return;
