	if( !fk ) {
		vi as;
		rep( i , 1 , n ) {
			int re = 0;
			for( int x : lsj[i] ) re += mu[x] * buc[x] , ++ buc[x];
			if( !re ) as.pb( i );
		}
		as.resize( k );
		for( int x : as ) printf("%d ",x); puts("");
		return;
	}
	vi as;
	era[fk] = 1;
	as.pb( fk );
	int ccn = 1;
	rep( i , 1 , n ) if( i != fk ) {
