{
	return x*p+y;
}
signed main()
{
	n=read();k=read();m=read();
	p=1<<m;up=id(k,p-1);
	for(int s=1;s<p;s++)
		bit[s]=bit[s>>1]+(s&1);
	for(int j=0;j<=k;j++)
	{
		for(int s=0;s<p;s++)
		{
			//不选的转移
			int s1=s>>1,s2=s1|(1<<m-1);
			if((s&1)==0)
			{
				A.a[id(j,s)][id(j,s1)]++;
				A.a[id(j,s)][id(j,s2)]++;
			}
			//选择的转移
			if(j==0 || (s&1)==0) continue;
			A.a[id(j,s)][id(j-1,s1)]+=(1+bit[s1]);
			A.a[id(j,s)][id(j-1,s2)]+=(1+bit[s2]);
		}
	}
	A=qkpow(A,n);
	for(int i=0;i<p;i++)
		ans=(ans+A.a[id(k,i)][id(0,0)])%MOD;
