	for(int i=1;i<=n;++i)
	{
		read(a[i]);
	}
	for(int i=2;i<=n;++i)
	{
		ans+=res[a[i-1]][a[i]];//计算交点数
	}
	for(int i=3;i<=n;++i)
	{
		if(a[i-1]==1&&a[i-2]==3&&a[i]==2)//特判特殊情况
