{
    e[++cnt] = {head[u], v, w};
    head[u] = cnt;
}
// 并查集
int find(int x, int y)  // 寻找祖先
{
    if (fa[x][y] == x)
        return x;
    return fa[x][y] = find(fa[x][y], y);
}
void join(int x, int y, int c)  // 合并
{
    int fx = find(x, c), fy = find(y, c);
    if (fx == fy)
        return;
    fa[fx][c] = fy;
    return;
}

// 遍历整棵树
void dfs(int u, int fa)
{
    for (int i = head[u]; i; i = e[i].nxt)
    {
        int v = e[i].to;
        if (v == fa)
            continue;
        join(u, v, e[i].w);
        dfs(v, u);
    }
}

