```cpp
#include<bits/stdc++.h>
using namespace std;

int tot=0,var[400010],len[400010],nxt[400010],head[200010];

void add(int u,int v,int w)
{
    var[++tot]=v;
    len[tot]=w;
    nxt[tot]=head[u];
    head[u]=tot;
}

int dp[200010][2];

void dfs1(int u,int fa) // 子树内
{
    for(int i=head[u]; i; i=nxt[i])
    {
        int v=var[i],w=len[i];
        if(v==fa) continue;
        dfs1(v,u);
        if(w==0)
        {
            dp[u][0]+=dp[v][0]+dp[v][1]+1;
        }
        else
        {
            dp[u][1]+=dp[v][1]+1;
        }
    }
}

void dfs2(int u,int fa) // 换根
{
    for(int i=head[u]; i; i=nxt[i])
    {
        int v=var[i],w=len[i];
        if(v==fa) continue;
        if(w==0)
        {
            dp[v][0]=dp[u][0]+dp[u][1]-dp[v][1];
        }
        else
        {
            dp[v][1]=dp[u][1];
        }
        dfs2(v,u);
    }
}

int main()
{
    int n,u,v,w;
    long long ans=0;
    scanf("%d",&n);
    for(int i=1; i<n; ++i)
    {
        scanf("%d%d%d",&u,&v,&w);
        add(u,v,w),add(v,u,w);
    }
    dfs1(1,0);
    dfs2(1,0);
    for(int i=1; i<=n; ++i)
    {
        ans+=dp[i][0]+dp[i][1];
    }
    printf("%lld",ans);
    return 0;
}
