{
	e[++tot]=(edge){v,w,h[u]};
	h[u]=tot;
}
bool vis[N];
int cor,ss,siz[N];
void getcor(int u,int fa,int all)
{
	int mxw=0;siz[u]=1;
	for(int i=h[u];i;i=e[i].nxt)
	{
		int v=e[i].v;
		if(v==fa||vis[v])continue;
