		mxw=max(mxw,siz[v]);
	}
	mxw=max(mxw,all-mxw);
	if(mxw<ss)ss=mxw,cor=u;
}
void getsiz(int u,int fa)
{
	siz[u]=1;
	for(int i=h[u];i;i=e[i].nxt)
	{
		int v=e[i].v;
		if(v==fa||vis[v])continue;
		getsiz(v,u),siz[u]+=siz[v]; 
