{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		int x;
		scanf("%d",&x);
		++a[x];
	}
	jc[1]=jc[0]=1;
	for(int i=2;i<=n;i++) jc[i]=jc[i-1]*i%mod;
	exgcd(jc[n],mod);
	inv=(x%mod+mod)%mod;
	s2[0]=1;
        int boo=0;
	for(int i=1;i<=n;i++)
		if(a[i])
		{
			if(a[i]>1)
			{
				boo=1;
				long long anss=0;
				for(int j=0;j<=k;j++) anss=(anss+jc[n-j-2]*s2[j])%mod;
				ans=(ans+(a[i]-1)*a[i]%mod*anss)%mod;
			}
			++k;
			for(int j=1;j<=k;j++) s1[j]=s2[j];
			s2[1]=s1[1]+a[i];
			for(int j=2;j<=k;j++) s2[j]=(s1[j]+s1[j-1]*a[i])%mod;
