	{
		cur=n-a[i];//cur是对a[i]的最佳匹配值。
		it=s.lower_bound(cur);//基于multiset的有序性在其中找和匹配值最接近的。
		if(it==s.end())//如果找不到那么就用第一个值。
		{
			it=s.begin();
			c[i]=(*it+a[i])%n;
			s.erase(it);
		}
		else
