template <typename T> inline T read(){
    T sum = 0;
    int fl = 1,ch = getchar();
    for(; !isdigit(ch); ch = getchar()) if(ch == '-') fl = -1;
    for(; isdigit(ch); ch = getchar()) sum = (sum << 3) + (sum << 1) + ch - '0';
    return sum * fl;
}

int x,y,z;

int main(){
	x = read<int>();
	y = read<int>();
	z = read<int>();
	if(x - z > y) {
		printf("+\n");
	}else if(y - z > x) { 
		printf("-\n");
	}else if(x == y && z == 0) {
	   	printf("0\n");
	}else printf("?\n");
	return 0;
}
