using namespace std;
int k,n,a,b;
//判断最大just play值为num时可取
int solve(int num,int i){  
	num+=i/b;
	if(i%b==0)num--;
	i=i%b;
	if(num>=n){
		return true;
	}else
	return false;
}
int main(){
	int T;scanf("%d",&T);
	while(T--){
		int i,j,num=0;
		scanf("%d%d%d%d",&k,&n,&a,&b);
		if(k%b==0)j=k/b-1;
		else j=k/b;
		if(j<n)printf("-1\n");
		else{
			if(k%a==0)j=k/a-1;
			else j=k/a;
			int zuo=0;int you=j,mid,ans=0;
			while(zuo<=you){
				mid=zuo+(you-zuo)/2;
				if(solve(mid,k-mid*a)){
					zuo=mid+1;ans=mid;
				}else you=mid-1;
			}printf("%d\n",min(n,ans));
		}
	}
}
