using namespace std;

int main()
{
	int T;
	cin>>T;
	while(T--)
	{
		int n,s,t;
		cin>>n>>s>>t;
		int a=s+t-n;
		int b=s-a,c=t-a;
		cout<<max(b,c)+1<<endl;
