		printf("0");
		return 0;
	}
	if(a[1].c!=a[2].c&&a[2].c!=a[3].c){//都不相等 
		printf("2");
		return 0;
	}
	if(a[1]==a[2]){//有两个相等
		printf("1");
		return 0;
	}
