		printf("1");
		return 0;
	}
	if(a[1].c==a[3].c&&a[1].s+2==a[3].s){//要加在中间
		printf("1");
		return 0;
	}
	if(a[1].c==a[2].c&&a[1].s+2==a[2].s){
		printf("1");
		return 0;
	}
	if(a[2].c==a[3].c&&a[2].s+2==a[3].s){
		printf("1");
		return 0;
	}
	printf("2");//都不满足
	return 0;
}
