		int n = read(),k = read();
		if(k % 3 != 0) {
			if(n % 3 != 0) printf("Alice\n");
			else printf("Bob\n");
		}
		else {
			n %= (k + 1);
			if(n == k||n % 3) printf("Alice\n");
			else printf("Bob\n");
		}
