			order[i].typ = 0;
			scanf("%d",&order[i].a);
		}
	}
	for(int i = q;i;i--){
		if(order[i].typ){
			int tar = order[i].a;
			if(!vis[tar]){
				vis[tar] = 1;
				ans[tar] = max(q2,order[i].b);
			}
		}
		else q2 = max(q2,order[i].a);
	}

	for(int i = 1;i <= n;i++){
		if(!vis[i])ans[i] = max(q2,ans[i]);
