#include<iostream>
#include<algorithm>
using namespace std;
int main()
{
    int n,a,b; bool k=0;
    cin>>a>>b>>n;
    while(n>0)
    {
        k==0?n-=__gcd(n,a):n-=__gcd(n,b);
        k=!k;//相反操作
    }
    cout<<!k;//这里要输出轮到的那一方，所以要再做一次相反操作
    return 0;
}
```

