	{
		cin>>c;//输入操作 
		if(c=='L')
		{
			t=0;//设置为左边第一个房间0 
			while(b[t]==1) t++; //找到第一个空房间 
			b[t]=1;//设置为有人 
		}
		if(c=='R')
		{
			t=9;//设置为最后一个房间9 
			while(b[t]==1) t--; //倒着找空房间 
			b[t]=1;//有人 
		}
		if(c>='0'&&c<='9')
		{
			t=c-'0';//把字符转化为数字 
			b[t]=0;//变为空房间 
		}
	}
	for(int i=0;i<10;i++) cout<<b[i];//输出答案 
	return 0;
}

```

