int vis[410];

int main () {
	cin >> n;
	for (int i = 1; i <= n; i++) {
		cin >> a[i];
	}
	sort (a+1, a+1+n);
	cin >> m;
	for (int i = 1; i <= m; i++) {
		cin >> b[i];
		vis[b[i]] = 1;
	}
	sort (b+1, b+1+m);
	cout << a[n] << " " << b[m] << endl;
	return 0;
}
