	char v = getchar();int x = 0,f = 1;
	while (!isdigit(v)) {if(v == '-')f = -1;v = getchar();}
	while (isdigit(v)) {x = x * 10 + v - 48;v = getchar();}
	return x * f;
}

const int N = 110;

int a[N],b[N],n,m,a1,a2;

int main() {
	n = read();
	for (int i = 1;i <= n;++i) {
		a[i] = read();
	}
	m = read();
	for (int i = 1;i <= m;++i) {
		b[i] = read();
	}
	for (int i = 1;i <= n;++i) {
		for (int j = 1;j <= m;++j) {
			a1 = a[i],a2 = b[j];
			int sum = a1 + a2;bool flag = 0;
			for (int k = 1;k <= n;++k) {
				if (sum == a[k]) {
					flag = 1;
					break;
				}
			}
			for (int k = 1;k <= m;++k) {
				if (sum == b[k]) {
					flag = 1;
					break;
				}
			}
			if (!flag) {
				printf("%d %d\n",a1,a2);
				return 0;
			}
		}
	}
	return 0;
}
