#include <bits/stdc++.h>
using namespace std;
int a[1005][1005];
int main()
{
	int n,cnt=-1; cin>>n;
	for(int i=1;i<=n/2;i++)
		for(int j=1;j<=n/2;j++)
		{
			a[i*2-1][j*2-1]=++cnt;
			a[i*2-1][j*2]=++cnt;
			a[i*2][j*2-1]=++cnt;
			a[i*2][j*2]=++cnt;
		}
	for(int i=1;i<=n;i++,putchar('\n'))
		for(int j=1;j<=n;j++)
			printf("%d ",a[i][j]);
	return 0;
}
```

