#include<bits/stdc++.h>
using namespace std;
int n,num,a[1005];
int main(){
    freopen("input.txt","r",stdin);
    freopen("output.txt","w",stdout);
    scanf("%d%d",&n,&num);
    for(int i=0;i<n;i++) scanf("%d",&a[i]);
    while(!a[(num-1)%n])  num++;
    printf("%d",(num-1)%n+1);
    return 0;
}
