```cpp
#include <cstdio>

typedef long long LL;

const LL INF = 0x3f3f3f3f3f3f3f3f;
const int MAXN = 4e5 + 5, MAXA = 25, MAXSIZ = ( 1 << 20 ) + 5;

template<typename _T>
void read( _T &x )
{
	x = 0;char s = getchar();int f = 1;
	while( s < '0' || '9' < s ){ f = 1; if( s == '-' ) f = -1; s = getchar(); }
	while( '0' <= s && s <= '9' ) { x = ( x << 3 ) + ( x << 1 ) + s - '0', s = getchar(); }
	x *= f;
}

template<typename _T>
void write( _T x )
{
	if( x < 0 ){ putchar( '-' ), x = -x; }
	if( 9 < x ) write( x / 10 );
	putchar( x % 10 + '0' );
}

template<typename _T>
_T ABS( const _T a )
{
	return a < 0 ? -a : a;
}

template<typename _T>
_T MIN( const _T a, const _T b )
{
	return a < b ? a : b;
}

LL DP[MAXSIZ];
LL ps[MAXA][MAXN], change[MAXA][MAXA], cost[MAXA], t[MAXA];
int usable[MAXA], pos[MAXA];
int a[MAXN], nxt[MAXN];
int N, tot;
bool appear[MAXA];

signed main()
{
	read( N );
	for( int i = 1 ; i <= N ; i ++ )
		read( a[i] ), appear[a[i]] = true;
	for( int i = 1 ; i <= 20 ; t[i] = 0, i ++ ) 	
		if( appear[i] )
			usable[++tot] = i, pos[i] = tot;
	for( int i = 1 ; i <= N ; i ++ )
		ps[pos[a[i]]][i] ++, cost[pos[a[i]]] += i - ( t[pos[a[i]]] ++ ) - 1;
	for( int i = 1 ; i <= N ; i ++ )
		for( int j = 1 ; j <= tot ; j ++ )
			ps[j][i] += ps[j][i - 1];
	for( int i = 1 ; i <= N ; i ++ )
		for( int j = 1 ; j <= tot ; j ++ )
			if( a[i] ^ usable[j] )
				change[pos[a[i]]][j] += ps[j][i - 1];
	int up = 1 << tot, pr;
	LL c; 
	DP[0] = 0;
	for( int S = 1 ; S < up ; S ++ )
	{
		DP[S] = INF;
		for( int i = 1 ; i <= tot ; i ++ )
			if( S & ( 1 << ( i - 1 ) ) )
			{
				c = cost[i];
				pr = S ^ ( 1 << ( i - 1 ) );
				for( int j = 1 ; j <= tot ; j ++ )
					if( pr & ( 1 << ( j - 1 ) ) )
						c -= change[i][j];
				DP[S] = MIN( DP[S], DP[pr] + c );
			}
	}
	write( DP[up - 1] ), putchar( '\n' );
	return 0;
}
