#include<bits/stdc++.h>
#include<iostream>
#include<cstdio>
#define ll long long
using namespace std;

const ll INF=1ll<<60;
ll n,a[30][30],g[1<<20][22],f[1<<20];
vector<ll> q[30];

inline ll read(){
	ll x=0,tmp=1;
	char ch=getchar();
	while(!isdigit(ch)){
		if(ch=='-') tmp=-1;
		ch=getchar();
	}
	while(isdigit(ch)){
		x=(x<<3)+(x<<1)+(ch^48);
		ch=getchar();
	}
	return tmp*x;
}

inline void write(ll x){
	if(x<0){
		putchar('-');
		x=-x;
	}
	ll y=10,len=1;
	while(y<=x){
		y=(y<<3)+(y<<1);
		len++;
	}
	while(len--){
		y/=10;
		putchar(x/y+48);
		x%=y;
	}
}

int main(){
	n=read();
	for(ll i=1; i<=n; i++) q[read()].push_back(i);
	for(ll i=1; i<=20; i++){
		for(ll j=1; j<=20; j++){
			if(i==j) continue;
			for(ll k=0,l=0; k<q[i].size(); k++){
				for(; l<q[j].size()&&q[i][k]>q[j][l]; l++);
				a[i][j]+=l;
			}
			ll s=(1<<(i-1))|(1<<(j-1));
			for(ll t=s; t<(1<<20); t=(t+1)|s) g[t][j]+=a[i][j];
		}
	}
	for(ll i=1; i<(1<<20); i++){
		f[i]=INF;
		for(ll j=1; j<=20; j++){
			if(i&(1<<(j-1))) f[i]=min(f[i],f[i^(1<<(j-1))]+g[i][j]);
		}
	}
	cout<<f[(1<<20)-1]<<endl;
	return 0;
}
