		for(int j=1;j<=n;j++)
			if(mp[i][j])fa[find(i)]=find(j);
	int ans=0;
	for(int i=1;i<=n;i++)
		if(fa[i]==i)ans++;
	return 1<<ans;
}
int mei01()
{
	int ans=1;
	for(int i=1;i<=n;i++)
	{
		bool flag=0;
		for(int j=1;j<=n;j++)
			if(mp[i][j])flag=1;
		if(!flag)ans=ans*2;
	}
	return ans;
}
void dfs(int u,int op)
{
	if(pd)return;
	vis[u]=op;
	for(int i=1;i<=n;i++)
		if(mp[u][i])
		{
			if(vis[i]==op)
			{
				pd=1;
				return;
			}
			else{
				if(vis[i]==-1)dfs(i,op^1);
			}
		}
}
int mei02()
{
	memset(vis,-1,sizeof(vis));
	int ans=0;
	for(int i=1;i<=n;i++)
		if(vis[i]==-1)
		{
			pd=0;
			dfs(i,0);
			if(pd)return 0;
			ans++;
		}
	return 1<<ans;
}
