    int main(){
        //read(1)
        //write(1)
        ios_base::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);

        cin>>q;
        REP(1,q,tt){
            cin>>n;
            if(n==2){//特判
                cout<<"2"<<endl;
                continue;
            }
            int m=n/2;
