    for(i=0;i<=s.length();i++){
        if(s[i]=='4'){
            sum1++;
        }else if(s[i]=='7'){
            sum2++;
        }
    }
    if((sum1==sum2)&&(sum1!=0)&&(sum2!=0)){
        cout<<"4";
        return 0;
    }else
    for(i=0;i<=s.length();i++){
        if((s[i]=='4')&&(k==0)){
            k++;
            x=i;
