        }
        if((s[i]=='4')&&(k==2)){
            k=0;
            s[x]='0';
            s[i]='0';
            if(ans!=0){
                i=ans;
                ans=0;
            }
        }
        if((s[i]=='7')&&(k==0)){
            k+=2;
            x=i;
            k2=0;
        }
        if((s[i]=='7')&&(k==1)){
            k=0;
            s[i]='0';
            s[x]='0';
            if(ans!=0){
                i=ans;
                ans=0;
            }
        }
        if((s[i]=='4')&&(k==1)&&(k2==0)){
            ans=i;
            k2=1;
        }
        if((s[i]=='7')&&(k==2)&&(k2==0)){
            ans=i;
            k2=1;
        }
    }
