			n4 ++;
		}
		if(s == '7')
		{
			n7 ++;
		}
	}
	if(n4 >= n7&&n4 != 0) cout << 4;//当4出现次数>=7出现次数输出4
	else if(n7 != 0) cout << 7; //否则输出7（4,7出现次数>0）
	else cout << -1;//当没有出现4,7就输出-1
	return 0;
} 
```
