    **/
    #include<bits/stdc++.h>
    #define REP(A,B,I) for(int I=(A);I<=(B);I++)
    #define PER(A,B,I) for(int I=(A);I>=(B);I--)
    #define max(X,Y) ((X)<(Y)?(Y):(X))
    #define min(Y,X) ((X)<(Y)?(X):(Y))
    #define read(FILENAME) freopen((FILENAME + ".txt"), "r", stdin);
    #define write(FILENAME) freopen((FILENAME + ".txt"), "w", stdout);
    #define LL long long
    #define N 1540
    using namespace std;
    int q,n,a[N],sum;
    int main(){
        //read(1)
        //write(1)
        ios_base::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);

        cin>>q;
        REP(1,q,ii){
            memset(a,0,sizeof(a));
            sum=0;
            cin>>n;
            REP(1,n,i){
                cin>>a[i];
                sum+=a[i];
            }
            int maxx=sum;
            maxx/=n;
            REP(maxx-5,maxx+5,i){
                if(i<=0)continue;
