inline int read()
{
 	int x(0),f(1);char c=getchar();
    while(c>'9'||c<'0')f=c=='-'?-1:1,c=getchar();
    while(c>='0'&&c<='9')x=x*10+c-48,c=getchar();
	return f*x;
}
int g[310][310],n;
int to(int a,int b)
{
	int cnt=0;
	for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)
	if(g[a][i]<g[b][j])cnt++;
	return cnt;
}
int main()
{
	//fr();
	freopen("a.out","r",stdin);
	n=read();
	for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)
	g[i][j]=read();
	int minn=INT_MAX;
	for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)
	if(i!=j)cerr<<i<<" "<<j<<" "<<to(i,j)<<endl,minn=min(minn,to(i,j));
	printf("%d",minn);
	return 0;
}

```

