inline void chmax(int &x,int y){x=max(x,y);}
const int N=(1<<18)+5;
int n;
ll a[N];
priority_queue<ll,vector<ll>,greater<ll>> pq;
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++) scanf("%lld",a+i);
	ll ans=0;
	for(int i=n;i>=1;i--){
		if(a[i]==-1) break;
		pq.push(a[i]);
		if(__builtin_popcount(i)==1){
			ans+=pq.top();
			pq.pop();
		}
	}
	printf("%lld\n",ans);
	return 0;
}
