		int n, k1, k2;
		scanf("%d%d%d", &n, &k1, &k2);
		int maxx1 = 0, maxx2 = 0;
		rep(i, 1, k1){
			int x;
			scanf("%d", &x);
			maxx1 = max(maxx1, x);
		}
		rep(i, 1, k2){
			int x;
