#include<bits/stdc++.h>
#define int long long
#define _int __int128
#define y1 _
using namespace std;

static char buf[1000000],*p1=buf,*p2=buf;

inline int read(){
	char c=getchar();
	int res=0,f=1;
	while(c<'0'||c>'9'){
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9'){
		res=res*10+c-'0';
		c=getchar();
	}
	return res*f;
}

inline void write(int x){
	static char buf[20];
	static int len=-1;
	if(x<0){
		putchar('-');
		x=-x;
	}
	do{
		buf[++len]=x%10;
		x/=10;
	}
	while(x);
	while(len>=0){
		putchar(buf[len--]+48);
	}
}

const int maxn=501000;
const int maxm=110;
const int mod=50;
const int inf=420444186480114514;

int n,m;
int a,b;
int cnta,cntb;

int visa[maxn],visb[maxn];

int head[maxn],tot;
struct egde{
	int to,nxt;
}e[maxn<<1];

void add(int u,int v){
	e[++tot].to=v;
	e[tot].nxt=head[u];
	head[u]=tot;
}

void dfs1(int u,int ed){
	visa[u]=1;
	for(int i=head[u];i;i=e[i].nxt){
		int v=e[i].to;
		if(!visa[v]&&v!=ed){
			dfs1(v,ed);
		} 
	}
}

void dfs2(int u,int ed){
	visb[u]=1;
	for(int i=head[u];i;i=e[i].nxt){
		int v=e[i].to;
		if(!visb[v]&&v!=ed){
			dfs2(v,ed);
		} 
	}
}

void init(){
	for(int i=1;i<=n;i++){
		visa[i]=0;
		visb[i]=0;
	}
	for(int i=1;i<=tot;i++){
		e[i].nxt=0;
		e[i].to=0;
		head[i]=0;
	}
	tot=0;
	cnta=0;
	cntb=0;
}

void solve(){
	init();
	n=read(),m=read(),a=read(),b=read();
	for(int i=1;i<=m;i++){
		int u=read(),v=read();
		add(u,v);
		add(v,u);
	}
	dfs1(a,b);
	dfs2(b,a);
	for(int i=1;i<=n;i++){
		if(i!=a){
			if(visa[i]&&!visb[i]){
				cnta++;
			}
		}
		if(i!=b){
			if(!visa[i]&&visb[i]){
				cntb++;
			}
		}
	}
	write(cnta*cntb);
	puts("");
	return ;
}

signed main(){
	int T=read();
	while(T--){
		solve();
	}
	return 0;
}



