	// a_1 <= b_1, a_i >= a[i+1], b_i <= b[i+1]
	For(i, 1, n) {
		dp[i][1][i] = 1;
		For(j, 1, m) {
			if(j != 1) 
				For(k, i, n)
					dp[i][j][k] = dp[i][j-1][k];
			For(k, i, n)
