{
	int i,j;
	cin>>n;
	n--;//先构造奇数列的矩阵，所以n先减一
	for(i=1; i<=n; i++)//先把第一行赋值了
		s[1][i]=i;
	for(i=1; i<n; i++)
	{
		s[i+1][n]=s[i][1];//把上一行的第一个元素挪到最后一个
		for(j=1; j<n; j++)
			s[i+1][j]=s[i][j+1];//其他元素都往前挪一个元素因为第一个元素被移走了
	}
	n++;
	for(i=1; i<=n; i++)
		s[n][i]=s[i][i],s[i][n]=s[i][i],s[i][i]=0;//把中轴的元素挪到两边，中轴添上0
	for(i=1; i<=n; i++)
	{
		for(j=1; j<=n; j++)
			cout<<s[i][j]<<" ";
		cout<<endl;
	}
	return 0;
}
