		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		x=x*10+ch-'0';
		ch=getchar();
	}
	return f*x;
}

int n;

signed main() {

	int t;
	t=read();
	while(t--) {
		n=read();
		cout<<1<<" "<<n-1<<endl;
	} 
	return 0;
}

```

