	{
		if(i&(1<<j))
		{
			siz[i][j]=(1<<(pp-1));
			dp[i][j].resize(1<<(pp-1));
		}
	}
}
//	dp[0][15][0]=1;
up(i,1,(1<<n)-1)
{
	int pp=__builtin_popcountll(i);
	up(j,0,n-1)
	{
		if(!(i&(1<<j)))continue;
		if(pp==1)dp[i][j][0]=1;
		up(x,0,siz[i][j]-1)
		{
			int y=dp[i][j][x];
			if(!y)continue;
			up(l,0,n-1)
			{
				if(i&(1<<l))continue;
				dp[i|(1<<l)][l][(x<<1)+(c[j][l]=='1')]+=y;
			}
		}
	}
}
up(j,0,n-1)
{
	up(t,0,siz[(1<<n)-1][j]-1)
	{
		ans[t]+=dp[(1<<n)-1][j][t];
	}
