		for(int k=1;k<=n;k++) if((i>>(k-1)&1)&&j!=k&&s[k][j]=='1'){
			dp[i][j]+=dp[i^(1<<j-1)][k];
		} g[bitcnt(i)][i]+=dp[i][j];
	}
//	for(int i=1;i<(1<<n);i++) printf("%lld\n",g[bitcnt(i)][i]);
	for(int i=1;i<=n;i++) FWTor(g[i],1<<n,1);
	for(int i=0;i<(1<<n);i++) mul[0][i]=1;dfs(1,1);
