		} v.pb(n-1-pre);sort(v.begin(),v.end());
//		for(int x:v) printf("%d ",x);printf("\n");
		ans[i]=ret[v];
	} FWTand(ans,1<<n-1,-1);
	for(int i=0;i<(1<<n-1);i++) printf("%lld ",ans[i]);
	return 0;
}
