	char ch=getchar();
	int f=1,x=0;
	while(ch<'0'||ch>'9') {
		if(ch=='-')
			f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') {
		x=x*10+ch-'0';
		ch=getchar();
	}
	return f*x;
}

int a,b,c,d,n;

signed main() {
	n=read();
	for(int i=1;i<=n;i++) {
		a=read();b=read();c=read();d=read();
		printf("%lld %lld %lld\n",a,c,c);
	}
	return 0;
}

```

