#include<cstdio>
using namespace std;
const int N=1e5+5;
struct edge
{
	int next,to;
}e[N<<1];
int n,cnt,ans1,ans2,in[N],dep[N],ind[N],lvson[N];
bool dis[N][2];
//这里第二维0/1表示当前点与叶结点的距离为偶数/奇数 
int read()
{
	int res,f=1;
	char ch;
	while((ch=getchar())<'0'||ch>'9')
	if(ch=='-')
	f=-1;
	res=ch^48;
	while((ch=getchar())>='0'&&ch<='9')
	res=(res<<1)+(res<<3)+(ch^48);
	return res*f;
}
void add(int x,int y)
{
	e[++cnt].next=in[x];
	e[cnt].to=y;
	ind[x]++;
	in[x]=cnt;
}
void dfs(int u,int f)
{
	int i,v;
	if(ind[u]==1)
	{
		dis[u][0]=1;
		if(u==1)
		lvson[e[in[u]].to]++;
		else
		lvson[f]++;
	}
	dep[u]=dep[f]+1;
	for(i=in[u];i;i=e[i].next)
	{
		v=e[i].to;
		if(v==f)
		continue;
		dfs(v,u);
		dis[u][0]|=dis[v][1];dis[u][1]|=dis[v][0];
		if((dis[u][0]&&dis[v][0])||(dis[u][1]&&dis[v][1]))
		ans1=3;//判断是否存在两叶子距离为奇数 
	}
}
int main()
{
	int i,x,y;
	n=read();
	for(i=1;i<=n-1;i++)
	{
		x=read();y=read();
		add(x,y);add(y,x);
	}
	ans1=1;ans2=n-1;
	dfs(1,0);
	for(i=1;i<=n;i++)
	if(lvson[i])
	ans2-=lvson[i]-1;
	printf("%d %d",ans1,ans2);
	return 0;
}
