#define ll long long
#define f(i,a,b) for(int i=a;i<=b;i++)
inline ll read(){
   int s=0,w=1;
   char ch=getchar();
   while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
   while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
   return s*w;
}
#define d read()
ll T;
ll n;
ll pw(ll x,ll y){
    ll ans=1;
    while(y){
        if(y&1)ans=ans*x;
        y>>=1;
        x*=x;
    }
    return ans-2;
}
int main(){
    T=d;
    while(T--){
        n=d;
        printf("%lld\n",pw(2,n/2+1));
    }
    return 0;
}


```

