#define pushup2(now)  tr[now].sq=(tr[lson].sq+tr[rson].sq);
using namespace std;
const int mod=100000000;
inline ll read() {
	ll now=0;
	char c=getchar();
	ll f=1;
	while(c<'0'||c>'9') {
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9') {
		now=(now<<1)+(now<<3)+(c^48);
		c=getchar();
	}
	return f*now;
}
int n,t,m,a[N];
signed main(){
	t=read();
	while(t--){
		n=read();m=read();int len=0;
		memset(a,0,sizeof(a));
		//memset(pos,0,sizeof(pos));
		for(int i=1;i<=n;i++)
		a[i]=read();
		for(int i=1;i<=n;i++){
			if(a[i]==m){
				len++;
			}
		}
		if(len==0){
			puts("no");
			continue;
		}
		if(n==1&&a[1]==m){
			puts("yes");
			continue;
		}
		bool flag=0;
		for(int i=1;i<n;i++){
			if(a[i]>=m && a[i+1]>=m){
				flag=1;break;
			}
			if(a[i-1]>=m && a[i]>=m){
				flag=1;break;
			}
			if(n>2&& a[i]>=m &&a[i-2]>=m){
				flag=1;break;
			}
			if(n>2&& a[i]>=m&&a[i+2]>=m){
				flag=1;break;
			}
		}
		if(flag)
		puts("yes");
		else
		{
			puts("no");
		}

	}
//	system("pause");
	return 0;
}
