int main(){
	cin>>n;
	for(register int i=0;i<n;++i){
		int x,y;
		cin>>x>>y;
		if(x&1==0)cout<<x+2*y<<endl;
		else{
		    for(int i=2;i<=x;i++)if(x%i==0){
				x+=i;
				break;
			}cout<<x+2*(y-1)<<endl;
		}
	}return 0;
}
