inline ll read() {
	ll now=0;
	char c=getchar();
	ll f=1;
	while(c<'0'||c>'9') {
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9') {
		now=(now<<1)+(now<<3)+(c^48);
		c=getchar();
	}
	return f*now;
}

int n,m,t;
struct tmp1 //运算符重载<
  {
      int x,id;
      tmp1(int a,int b) {x = a;id=b;}
     bool operator<(const tmp1& a) const
	      {
        if(x==a.x)return id>a.id;
			return x<a.x;
     }
 };
priority_queue<tmp1> s;
int a[200005];
signed main(){
	//freopen("test.in","r",stdin);
	t=read();
	while(t--){
		n=read();
		memset(a,0,sizeof(a));//初始化
		s.push(tmp1(n,1));
		int step=1;
		while(!s.empty()){
			int l=s.top().id;
			int r=l+s.top().x-1;
			s.pop();
		//	cout<<l<<" "<<r<<endl;
			if((l+r)%2){ //判断奇数偶数
				int mid=(l+r-1)>>1;
				a[mid]=step;step++;
				if(l==r)continue;
				if(l<mid)
				s.push(tmp1(mid-l,l));
				if(mid<r)//判断
				s.push(tmp1(r-mid,mid+1));//压队列
			}
			else{
				int mid=(l+r)>>1;
				a[mid]=step;step++;
				if(l==r)continue;
				if(l<mid)
				s.push(tmp1(mid-l,l));
				if(mid<r)
				s.push(tmp1(r-mid,mid+1));
			}
		}
		for(int i=1;i<=n;i++)
		cout<<a[i]<<" ";
		cout<<endl;
	}
	return 0;
} 
